// Accepts various date inputs and returns a consistently formatted date string
function getFormattedDate(date: Date | string | number): string {
  const newDate = new Date(date)
  if (!isNaN(newDate.getTime())) {
    // Produces a consistent, ISO-formatted date string
    return newDate.toISOString().split('T')[0]
  }
  return '1970-01-01' // Default date for invalid inputs
}

