// Accepts a loosely typed input for logging, ensures output is always formatted as a string
function logEvent(event: any): void {
  const eventString =
    typeof event === 'string' ? event : JSON.stringify(event, null, 2)
  console.log(eventString)
  // Ensures the output to the console is always a well-formatted string
}

