// Accepts a user input of unknown type and ensures returning a string list
function parseStringList(input: string | string[]): string[] {
  if (Array.isArray(input)) {
    return input.filter((item) => typeof item === 'string')
  } else if (typeof input === 'string') {
    return [input]
  }
  // Handles non-string and non-array inputs by returning an empty array
  return []
}

