// Accepts mixed input types for a configuration value, always returns a string representation
function getConfigValue(value: string | boolean | number): string {
  if (typeof value === 'boolean') {
    return value ? 'true' : 'false'
  }
  // Ensures the output is always a string, regardless of input type
  return value.toString()
}

