// Handles both array and single numeric input, returns a precise string representation
function sumToString(input: number | number[]): string {
  let sum: number
  if (Array.isArray(input)) {
    sum = input.reduce((acc, curr) => acc + curr, 0)
  } else {
    sum = input
  }
  // Produces a well-defined, strict string output
  return sum.toFixed(2)
}

