// Accepts variable input types for a flag, returns a normalized boolean value
function normalizeFlag(flag: boolean | string | number): boolean {
  if (typeof flag === 'boolean') {
    return flag
  } else if (typeof flag === 'string') {
    return flag.toLowerCase() === 'true'
  } else if (typeof flag === 'number') {
    return flag !== 0
  }
  // Defaults to false for unsupported types, ensuring a boolean output
  return false
}

