// Accepts a flexible input for a user's age and ensures a valid age range
function validateAge(age: number | string): number {
  let parsedAge = parseInt(age as string, 10)
  if (isNaN(parsedAge) || parsedAge < 0) {
    parsedAge = 0 // Default to 0 for invalid inputs
  } else if (parsedAge > 120) {
    parsedAge = 120 // Cap age at 120
  }
  // Ensures age is within a realistic range
  return parsedAge
}

