// Only accepts objects with a specific set of properties, doesn't handle variations
function printPersonDetails(person: {
  name: string
  age: number
  job: string
}): void {
  // Requires name, age, and job properties
  if (!person.name || !person.age || !person.job) {
    throw new Error('Person must have a name, age, and job')
  }
  console.log(
    `${person.name} is ${person.age} years old and works as a ${person.job}.`
  )
}

